import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import AdminPanel from './components/AdminPanel';
import AuthModal from './components/AuthModal';
import ProductCatalog from './components/ProductCatalog';
import CartModal from './components/CartModal';
import AdminAccess from './components/AdminAccess';

const API = process.env.REACT_APP_BACKEND_URL ? `${process.env.REACT_APP_BACKEND_URL}/api` : 'http://localhost:8000/api';

// Componentes (mant茅n tus componentes existentes como Toast, Header, HeroSection, etc.)
const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 5000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`toast toast-${type}`}>
      <span>{message}</span>
      <button onClick={onClose} className="toast-close"></button>
    </div>
  );
};

const Header = ({ user, onLogout, cartItemsCount, onShowAuth, onShowCart, onShowAdmin }) => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <span className="logo-text"> Farmachelo</span>
          </div>

          <nav className="nav">
            <button onClick={() => scrollToSection('productos')} className="nav-link">
              Productos
            </button>
            <button onClick={() => scrollToSection('categorias')} className="nav-link">
              Categor铆as
            </button>
            <button onClick={() => scrollToSection('contacto')} className="nav-link">
              Contacto
            </button>
          </nav>

          <div className="header-actions">
            {user ? (
              <>
                <button onClick={onShowCart} className="cart-button">
                   Carrito ({cartItemsCount})
                </button>
                <span className="user-greeting">Hola, {user.name}</span>
                <button onClick={onLogout} className="logout-button">Cerrar Sesi贸n</button>
              </>
            ) : (
              <div className="auth-buttons">
                <button onClick={() => onShowAuth('login')} className="login-button">
                  Iniciar Sesi贸n
                </button>
                <button onClick={() => onShowAuth('register')} className="register-button">
                  Registrarse
                </button>
              </div>
            )}

            {/* Bot贸n de acceso a administraci贸n (solo admin) */}
            {user?.is_admin && (
              <button 
                onClick={onShowAdmin} 
                className="admin-access-btn"
                title="Acceso a Panel de Administraci贸n"
              >
                锔 Admin
              </button>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

const HeroSection = () => {
  const scrollToProducts = () => {
    const element = document.getElementById('productos');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">Tu Farmacia Online de Confianza</h1>
          <p className="hero-subtitle">
            Medicamentos con y sin receta, entrega r谩pida y segura. 
            M谩s de 1000 productos farmac茅uticos al mejor precio.
          </p>
          <div className="hero-buttons">
            <button onClick={scrollToProducts} className="cta-primary">Ver Productos</button>
            <button onClick={() => scrollToSection('sobre-nosotros')} className="cta-secondary">Conoce M谩s</button>
          </div>
        </div>
        <div className="hero-image">
          <div className="hero-placeholder">
            <span></span>
            <p>Farmacia Moderna</p>
          </div>
        </div>
      </div>
    </section>
  );
};

// ... (mant茅n tus otros componentes como ProductCard, ProductCatalog, etc.)

const App = () => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [toasts, setToasts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAdmin, setShowAdmin] = useState(false);
  const [status, setStatus] = useState(null);

  // El acceso de admin se basa en user.is_admin y el JWT normal

  useEffect(() => {
    const savedToken = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    if (savedToken && savedUser) {
      setToken(savedToken);
      const parsedUser = JSON.parse(savedUser);
      setUser(parsedUser);
      // No mostrar admin autom�aticamente, solo si el usuario lo solicita
    }

    // Si no hay token intentar cargar carrito local
    if (!savedToken) {
      const localCart = localStorage.getItem('cart');
      if (localCart) {
        try {
          const parsed = JSON.parse(localCart);
          setCart({
            id: parsed.id || null,
            user_id: null,
            items: parsed.items || [],
            updated_at: parsed.updated_at || null
          });
        } catch (e) {
          console.error('Error parsing local cart:', e);
        }
      }
    }

    setLoading(false);
  }, []);

  useEffect(() => {
    const loadProducts = async () => {
      try {
        const response = await axios.get(`${API}/products`);
        setProducts(response.data);
      } catch (error) {
        console.error('Error cargando productos:', error);
      }
    };

    loadProducts();
  }, []);

  useEffect(() => {
    if (user && token) {
      loadCart();
    }
  }, [user, token]);

  // Sincronizar carrito con localStorage para payment.html (siempre, aunque no haya usuario logueado)
  useEffect(() => {
    if (cart && cart.items && cart.items.length > 0) {
      // Si es carrito local (user_id null) guardamos la estructura completa
      const isLocal = !cart.user_id;
      if (isLocal) {
        const cartData = {
          items: cart.items.map(item => ({
            product_id: item.product_id || item.id,
            id: item.product_id || item.id,
            name: item.name,
            price: item.price,
            quantity: item.quantity,
            image_url: item.image_url || null,
            requires_prescription: !!item.requires_prescription
          })),
          total: cart.items.reduce((s, it) => s + (Number(it.price || 0) * (it.quantity || 0)), 0),
          updated_at: cart.updated_at || new Date().toISOString()
        };
        localStorage.setItem('cart', JSON.stringify(cartData));
      } else {
        // Para carrito de usuario (backend), guardamos un resumen m铆nimo para payment.html
        const resumenCart = {
          items: cart.items.map(item => ({
            name: item.name,
            price: item.price,
            quantity: item.quantity
          }))
        };
        localStorage.setItem('cart', JSON.stringify(resumenCart));
      }
    } else {
      localStorage.removeItem('cart');
    }
  }, [cart]);

  useEffect(() => {
    fetch('/api/health')
      .then(res => res.json())
      .then(data => setStatus(data))
      .catch(() => setStatus({ status: 'unreachable' }));
  }, []);

  const addToast = (message, type = 'info') => {
    const id = Date.now();
    setToasts(prev => [...prev, { id, message, type }]);
  };

  const removeToast = (id) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  const loadCart = async () => {
    try {
      const response = await axios.get(`${API}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Asegurar que los items del carrito tengan la estructura correcta
      const cartData = {
        ...response.data,
        items: response.data.items ? response.data.items.map(item => ({
          ...item,
          name: item.name || 'Producto',
          price: item.price || 0,
          image_url: item.image_url || null,
          requires_prescription: item.requires_prescription || false
        })) : []
      };

      setCart(cartData);
    } catch (error) {
      console.error('Error cargando carrito:', error);
    }
  };

  const handleLogin = (userData, userToken) => {
    setUser(userData);
    setToken(userToken);
    if (!userData?.is_admin) {
      addToast(`隆Bienvenido ${userData.name}!`, 'success');
    } else {
      setShowAdmin(true);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setToken(null);
    setCart(null);
    setShowAdmin(false);
    addToast('Sesi贸n cerrada correctamente', 'info');
  };

  const handleShowAuth = (mode) => {
    setAuthMode(mode);
    setShowAuth(true);
  };

  const handleAddToCart = async (product) => {
    // Si no hay usuario autenticado guardamos el carrito localmente
    if (!user || !token) {
      try {
        const raw = localStorage.getItem('cart');
        const local = raw ? JSON.parse(raw) : { items: [], total: 0 };

        const existingIndex = local.items.findIndex(it => (it.product_id || it.id) === product.id);
        if (existingIndex !== -1) {
          local.items[existingIndex].quantity = (local.items[existingIndex].quantity || 0) + 1;
        } else {
          local.items.push({
            product_id: product.id,
            id: product.id,
            name: product.name,
            price: Number(product.price) || 0,
            quantity: 1,
            image_url: product.image_url || null,
            requires_prescription: product.requires_prescription || false
          });
        }

        local.total = local.items.reduce((s, it) => s + (Number(it.price || 0) * (it.quantity || 0)), 0);
        local.updated_at = new Date().toISOString();

        localStorage.setItem('cart', JSON.stringify(local));

        setCart({ id: null, user_id: null, items: local.items, updated_at: local.updated_at });
        addToast(`${product.name} agregado al carrito (guardado localmente)`, 'success');
        return;
      } catch (e) {
        console.error('Error agregando al carrito local:', e);
        addToast('No se pudo agregar el producto al carrito local', 'error');
        return;
      }
    }

    // Usuario autenticado: petici\u00f3n al backend
    try {
      const response = await axios.post(`${API}/cart/items`, {
        product_id: product.id,
        quantity: 1
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const updatedCart = {
        ...response.data,
        items: response.data.items.map(item => ({
          ...item,
          name: item.name || product.name,
          price: item.price || product.price,
          image_url: item.image_url || product.image_url,
          requires_prescription: item.requires_prescription || product.requires_prescription
        }))
      };

      setCart(updatedCart);
      addToast(`${product.name} agregado al carrito`, 'success');
    } catch (error) {
      console.error('Error adding to cart:', error);
      addToast('Error al agregar producto al carrito', 'error');
    }
  };

  const cartItemsCount = cart ? cart.items.reduce((total, item) => total + item.quantity, 0) : 0;

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loader"></div>
        <p>Cargando Farmachelo...</p>
      </div>
    );
  }

  return (
    <BrowserRouter>
      <div className="App">
        {/* Notificaciones Toast */}
        <div className="toast-container">
          {toasts.map(toast => (
            <Toast
              key={toast.id}
              message={toast.message}
              type={toast.type}
              onClose={() => removeToast(toast.id)}
            />
          ))}
        </div>

        {showAdmin ? (
          <AdminPanel onBack={() => setShowAdmin(false)} />
        ) : (
          <>
            <Header 
              user={user} 
              onLogout={handleLogout} 
              cartItemsCount={cartItemsCount}
              onShowAuth={handleShowAuth}
              onShowCart={() => setShowCart(true)}
              onShowAdmin={() => {
                if (user?.is_admin) setShowAdmin(true);
                else addToast('Acceso solo para administradores', 'warning');
              }}
            />

            <HeroSection />

            {/* Cat谩logo de productos */}
            <ProductCatalog 
              products={products} 
              onAddToCart={handleAddToCart}
              user={user}
            />

            {/* Secci贸n de categor铆as */}
            <section id="categorias" className="categories-section">
              <div className="container">
                <h2 className="section-title">Categor铆as</h2>
                <div className="categories-grid">
                  <div className="category-card">
                    <div className="category-icon"></div>
                    <h3>Con Receta</h3>
                    <p>Medicamentos que requieren prescripci贸n m茅dica</p>
                  </div>
                  <div className="category-card">
                    <div className="category-icon"></div>
                    <h3>Sin Receta</h3>
                    <p>Medicamentos de venta libre y productos de salud</p>
                  </div>
                </div>
              </div>
            </section>

            {/* Secci贸n de contacto */}
            <section id="contacto" className="contact-section">
              <div className="container">
                <h2 className="section-title">Contacto</h2>
                <div className="contact-info">
                  <div className="contact-item">
                    <span className="contact-icon"></span>
                    <div>
                      <h4>Tel茅fono</h4>
                      <p>+57 300 123 4567</p>
                    </div>
                  </div>
                  <div className="contact-item">
                    <span className="contact-icon"></span>
                    <div>
                      <h4>Email</h4>
                      <p>info@farmachelo.com</p>
                    </div>
                  </div>
                  <div className="contact-item">
                    <span className="contact-icon"></span>
                    <div>
                      <h4>Direcci贸n</h4>
                      <p>Calle 123 #45-67, Bogot谩, Colombia</p>
                    </div>
                  </div>
                </div>
              </div>
            </section>

            {/* Modales */}
            <AuthModal
              isOpen={showAuth}
              onClose={() => setShowAuth(false)}
              onLogin={handleLogin}
              mode={authMode}
              onModeChange={setAuthMode}
            />

            <CartModal
              isOpen={showCart}
              onClose={() => setShowCart(false)}
              cart={cart}
              onUpdateCart={setCart}
              token={token}
            />
          </>
        )}
      </div>
    </BrowserRouter>
  );
};

export default App;
