import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import AdminPanel from './components/AdminPanel';

const API = `${process.env.REACT_APP_BACKEND_URL}/api`;
const [showAdmin, setShowAdmin] = useState(false);

//===================ADMIN====================

const checkAdminAccess = (email) => {
  // Lista de emails autorizados como administradores
  const adminEmails = ['admin@farmachelo.com', 'plbarm1209@gmail.com'];
  return adminEmails.includes(email);
};

// ==================== COMPONENTES ====================

const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 5000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`toast toast-${type}`}>
      <span>{message}</span>
      <button onClick={onClose} className="toast-close">√ó</button>
    </div>
  );
};

const Header = ({ user, onLogout, cartItemsCount, onShowAuth, onShowCart }) => {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <span className="logo-text">üè• Farmachelo</span>
          </div>
          
          <nav className="nav">
            <a href="#productos" className="nav-link">Productos</a>
            <a href="#categorias" className="nav-link">Categor√≠as</a>
            <a href="#contacto" className="nav-link">Contacto</a>
          </nav>

          <div className="header-actions">
            {user ? (
              <>
                <button onClick={onShowCart} className="cart-button">
                  üõí Carrito ({cartItemsCount})
                </button>
                <span className="user-greeting">Hola, {user.name}</span>
                <button onClick={onLogout} className="logout-button">Cerrar Sesi√≥n</button>
              </>
            ) : (
              <div className="auth-buttons">
                <button onClick={() => onShowAuth('login')} className="login-button">
                  Iniciar Sesi√≥n
                </button>
                <button onClick={() => onShowAuth('register')} className="register-button">
                  Registrarse
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

const HeroSection = () => {
  return (
    <section className="hero">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">Tu Farmacia Online de Confianza</h1>
          <p className="hero-subtitle">
            Medicamentos con y sin receta, entrega r√°pida y segura. 
            M√°s de 1000 productos farmac√©uticos al mejor precio.
          </p>
          <div className="hero-buttons">
            <a href="#productos" className="cta-primary">Ver Productos</a>
            <a href="#sobre-nosotros" className="cta-secondary">Conoce M√°s</a>
          </div>
        </div>
        <div className="hero-image">
          <div className="hero-placeholder">
            <span>üè•</span>
            <p>Farmacia Moderna</p>
          </div>
        </div>
      </div>
    </section>
  );
};

const ProductCard = ({ product, onAddToCart, user }) => {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    if (!user) {
      alert('Por favor inicia sesi√≥n para agregar productos al carrito');
      return;
    }
    onAddToCart(product.id, quantity);
  };

  return (
    <div className="product-card">
      <div className="product-image">
        <img src={product.image_url} alt={product.name} />
        {product.requires_prescription && (
          <span className="prescription-badge">Requiere Receta</span>
        )}
      </div>
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <p className="product-description">{product.description}</p>
        <div className="product-price">${product.price}</div>
        <div className="product-stock">Stock: {product.stock}</div>
        
        <div className="product-actions">
          <div className="quantity-selector">
            <button 
              onClick={() => setQuantity(Math.max(1, quantity - 1))}
              className="quantity-btn"
            >
              -
            </button>
            <span className="quantity">{quantity}</span>
            <button 
              onClick={() => setQuantity(quantity + 1)}
              className="quantity-btn"
            >
              +
            </button>
          </div>
          <button 
            onClick={handleAddToCart}
            disabled={product.stock === 0}
            className="add-to-cart-btn"
          >
            {product.stock === 0 ? 'Sin Stock' : 'Agregar al Carrito'}
          </button>
        </div>
      </div>
    </div>
  );
};

const ProductCatalog = ({ products, onAddToCart, user }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  
  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = categoryFilter === '' || product.category === categoryFilter;
    return matchesSearch && matchesCategory;
  });

  return (
    <section id="productos" className="product-catalog">
      <div className="container">
        <h2 className="section-title">Nuestros Productos</h2>
        
        <div className="catalog-filters">
          <input
            type="text"
            placeholder="Buscar medicamentos..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
            className="category-filter"
          >
            <option value="">Todas las categor√≠as</option>
            <option value="over_counter">Sin Receta</option>
            <option value="prescription">Con Receta</option>
          </select>
        </div>

        <div className="products-grid">
          {filteredProducts.map(product => (
            <ProductCard
              key={product.id}
              product={product}
              onAddToCart={onAddToCart}
              user={user}
            />
          ))}
        </div>
        
        {filteredProducts.length === 0 && (
          <div className="no-products">
            <p>No se encontraron productos que coincidan con tu b√∫squeda.</p>
          </div>
        )}
      </div>
    </section>
  );
};

const AuthModal = ({ mode, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    address: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const endpoint = mode === 'login' ? '/auth/login' : '/auth/register';
      const response = await axios.post(`${API}${endpoint}`, formData);
      
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      
      onSuccess(response.data.user, response.data.token);
      onClose();
    } catch (err) {
      setError(err.response?.data?.detail || 'Error en la autenticaci√≥n');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <div className="modal-header">
          <h2>{mode === 'login' ? 'Iniciar Sesi√≥n' : 'Registrarse'}</h2>
          <button onClick={onClose} className="close-button">√ó</button>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {error && <div className="error-message">{error}</div>}
          
          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
          />
          
          <input
            type="password"
            placeholder="Contrase√±a"
            value={formData.password}
            onChange={(e) => setFormData({...formData, password: e.target.value})}
            required
          />

          {mode === 'register' && (
            <>
              <input
                type="text"
                placeholder="Nombre completo"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                required
              />
              <input
                type="tel"
                placeholder="Tel√©fono (opcional)"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
              />
              <textarea
                placeholder="Direcci√≥n (opcional)"
                value={formData.address}
                onChange={(e) => setFormData({...formData, address: e.target.value})}
              />
            </>
          )}

          <button type="submit" disabled={loading} className="submit-btn">
            {loading ? 'Procesando...' : (mode === 'login' ? 'Iniciar Sesi√≥n' : 'Registrarse')}
          </button>
        </form>
      </div>
    </div>
  );
};

const Cart = ({ cart, products, onRemoveFromCart, onClose }) => {
  const getProductDetails = (productId) => {
    return products.find(p => p.id === productId);
  };

  const calculateTotal = () => {
    return cart.items.reduce((total, item) => {
      const product = getProductDetails(item.product_id);
      return total + (product ? product.price * item.quantity : 0);
    }, 0);
  };

  if (!cart || cart.items.length === 0) {
    return (
      <div className="modal-backdrop">
        <div className="modal-content">
          <div className="modal-header">
            <h2>Tu Carrito</h2>
            <button onClick={onClose} className="close-button">√ó</button>
          </div>
          <div className="empty-cart">
            <p>Tu carrito est√° vac√≠o</p>
            <button onClick={onClose} className="continue-shopping">
              Continuar Comprando
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Tu Carrito</h2>
          <button onClick={onClose} className="close-button">√ó</button>
        </div>
        
        <div className="cart-items">
          {cart.items.map(item => {
            const product = getProductDetails(item.product_id);
            if (!product) return null;
            
            return (
              <div key={item.product_id} className="cart-item">
                <img src={product.image_url} alt={product.name} className="cart-item-image" />
                <div className="cart-item-details">
                  <h4>{product.name}</h4>
                  <p>Cantidad: {item.quantity}</p>
                  <p>Precio: ${product.price}</p>
                  <p>Subtotal: ${(product.price * item.quantity).toFixed(2)}</p>
                </div>
                <button 
                  onClick={() => onRemoveFromCart(item.product_id)}
                  className="remove-item-btn"
                >
                  Eliminar
                </button>
              </div>
            );
          })}
        </div>
        
        <div className="cart-footer">
          <div className="cart-total">
            <strong>Total: ${calculateTotal().toFixed(2)}</strong>
          </div>
          <div className="cart-actions">
            <button onClick={onClose} className="continue-shopping">
              Continuar Comprando
            </button>
            <button 
              onClick={() => alert('Funci√≥n de pago pr√≥ximamente')} 
              className="checkout-btn"
            >
              Proceder al Pago
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// ==================== APLICACI√ìN PRINCIPAL ====================

const App = () => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [toasts, setToasts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Inicializar autenticaci√≥n
  useEffect(() => {
    const savedToken = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    
    if (savedToken && savedUser) {
      setToken(savedToken);
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  // Cargar productos
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const response = await axios.get(`${API}/products`);
        setProducts(response.data);
      } catch (error) {
        console.error('Error cargando productos:', error);
        addToast('Error cargando productos. Verifique la conexi√≥n con el servidor.', 'error');
      }
    };

    loadProducts();
  }, []);

  // Cargar carrito cuando el usuario inicia sesi√≥n
  useEffect(() => {
    if (user && token) {
      loadCart();
    }
  }, [user, token]);

  const addToast = (message, type = 'info') => {
    const id = Date.now();
    setToasts(prev => [...prev, { id, message, type }]);
  };

  const removeToast = (id) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  const loadCart = async () => {
    try {
      const response = await axios.get(`${API}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
    } catch (error) {
      console.error('Error cargando carrito:', error);
    }
  };

const handleLogin = (userData, userToken) => {
  setUser(userData);
  setToken(userToken);
  addToast(`¬°Bienvenido ${userData.name}!`, 'success');
  
  // Redirigir a panel de administraci√≥n si es admin
  if (checkAdminAccess(userData.email)) {
    setShowAdmin(true);
  }
};

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setToken(null);
    setCart(null);
    addToast('Sesi√≥n cerrada correctamente', 'info');
  };

  const handleAddToCart = async (productId, quantity) => {
    try {
      await axios.post(`${API}/cart/add`, {
        product_id: productId,
        quantity: quantity
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      await loadCart();
      addToast('Producto agregado al carrito', 'success');
    } catch (error) {
      console.error('Error agregando al carrito:', error);
      addToast('Error agregando producto al carrito', 'error');
    }
  };

  const handleRemoveFromCart = async (productId) => {
    try {
      await axios.delete(`${API}/cart/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      await loadCart();
      addToast('Producto eliminado del carrito', 'info');
    } catch (error) {
      console.error('Error eliminando del carrito:', error);
      addToast('Error eliminando producto del carrito', 'error');
    }
  };

  const handleShowAuth = (mode) => {
    setAuthMode(mode);
    setShowAuth(true);
  };

  const cartItemsCount = cart ? cart.items.reduce((total, item) => total + item.quantity, 0) : 0;

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loader"></div>
        <p>Cargando FarmaWeb...</p>
      </div>
    );
  }

return (
  <BrowserRouter>
    <div className="App">
      {/* Notificaciones Toast */}
      <div className="toast-container">
        {toasts.map(toast => (
          <Toast
            key={toast.id}
            message={toast.message}
            type={toast.type}
            onClose={() => removeToast(toast.id)}
          />
        ))}
      </div>

      {showAdmin ? (
        <AdminPanel onBack={() => setShowAdmin(false)} />
      ) : (
        <>
          <Header 
            user={user} 
            onLogout={handleLogout} 
            cartItemsCount={cartItemsCount}
            onShowAuth={handleShowAuth}
            onShowCart={() => setShowCart(true)}
          />
          
          <HeroSection />
          
          <ProductCatalog 
            products={products}
            onAddToCart={handleAddToCart}
            user={user}
          />

          {/* Modales */}
          {showCart && (
            <Cart
              cart={cart}
              products={products}
              onRemoveFromCart={handleRemoveFromCart}
              onClose={() => setShowCart(false)}
            />
          )}

          {showAuth && (
            <AuthModal
              mode={authMode}
              onClose={() => setShowAuth(false)}
              onSuccess={handleLogin}
            />
          )}
        </>
      )}
    </div>
  </BrowserRouter>
);
};

export default App;
